import React, { Component } from 'react';
import { Button, Form } from 'semantic-ui-react';
import { runInThisContext } from 'vm';

class Authbox extends Component {
    state = {
        password: 'yes',
        confirmpassword: ''
    };

    constructor() {
        super();
        this.handleChange = this.handleChange.bind(this);

    handleSubmit = (event) => {
        this.setState(
            {
                confirmpassword: event.target.value
            }
        )
        console.log(event.target.value);
        if (this.state.confirmpassword !== this.state.password) {
          alert("Nope");
        }
    }
    }

    handleChange(e) {
        this.setState(
            {
                [e.target.name]: e.target.value
            }
        )
        // console.log(this);
    }

    render() {
        return (
            <div>
                <Form onSubmit={this.handleSubmit}>
                <Form.Input style={{width:'250px', marginBottom:'30px'}} fluid icon='lock' type='password' name="password" iconPosition='left' placeholder='password' onChange={this.handleChange}/>
                <Button style={{margin: '100px'}} onClick={this.handleSubmit}>Button</Button>
                </Form>
            </div>
        );
    }
}

export default Authbox;
