import { Link } from "react-router-dom";
import {Moon, Female, Leaf, Sun} from '../components/Labels/Labels'
import decode from 'jwt-decode';

class OneWine extends Component {


  state = {
    wine: {},
    wines: [],
    open: false,
    isLoaded: false,
    pdfcode: "",
    industry: false
