  constructor() {
    this.fetch = this.fetch.bind(this) // React binding stuff
    this.login = this.login.bind(this)
    this.getProfile = this.getProfile.bind(this)
}

login(username, password) {
    // Get a token from api server using the fetch api
    // console.log('logindomain', this.domain);
    return this.fetch(`/api/login`, {
        method: 'POST',
        body: JSON.stringify({
            username,
            password
        })
    }).then(res => {
        this.setToken(res.token) // Setting the token in localStorage
        return Promise.resolve(res);
    })
}

loggedIn() {
    // Checks if there is a saved token and it's still valid
    const token = this.getToken() // GEtting token from localstorage
    // console.log('token', token);
    const loggedIn = !!token && !this.isTokenExpired(token);
    !loggedIn && !sessionStorage.getItem('referrer') && sessionStorage.setItem('referrer', window.location.pathname);
    return loggedIn; // handwaiving here
}

isTokenExpired(token) {
    try {
        const decoded = decode(token);
        // console.log("decoded", decoded);
        if (decoded.exp < Date.now() / 1000) { // Checking if token is expired. N
            return true;
        }
        else
            return false;
    }
    catch (err) {
        return false;
    }
}

setToken(idToken) {
    // Saves user token to localStorage
    localStorage.setItem('id_token', idToken)
}

getToken() {
    // Retrieves the user token from localStorage
    return localStorage.getItem('id_token')
}

logout() {
    // Clear user token and profile data from localStorage
    localStorage.removeItem('id_token');
}

getProfile() {
    // Using jwt-decode npm package to decode the token
    return decode(this.getToken());
}


fetch(url, options) {
    // performs api calls sending the required authentication headers
    const headers = {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
    }

    // Setting Authorization header
    // Authorization: Bearer xxxxxxx.xxxxxxxx.xxxxxx
    if (this.loggedIn()) {
        headers['Authorization'] = 'Bearer ' + this.getToken()
    }

    return fetch(url, {
        headers,
        ...options
    })
        .then(this._checkStatus)
        .then(response => response.json())
}

_checkStatus(response) {
    // raises an error in case response status is not a success
    if (response.status >= 200 && response.status < 300) { // Success status lies between 200 to 300
        return response
    } else {
        var error = new Error(response.statusText)
        error.response = response
        throw error
    }
}

