   </Table.Body>
  </Table>
 </Grid.Column>
</Grid.Row>


{/* Producer's Other Wines */}
{(this.state.wines.length === 0)? <div></div>:

<Grid.Row>
<Grid.Column width={12}>
<Header as='h3'>{this.state.wine.Producer}'s Other Wines</Header>

       <Grid.Row>  

         <Grid.Column width={8}>

