import React, { Component } from "react";
import { Grid, Table } from "semantic-ui-react";


class Pricing extends Component {
    render() {

        const prequantity = this.props.caseSize;
        const qty = prequantity.split("x");
        const quantity = qty[0];
        const bottleCost = (this.props.price / quantity).toFixed(2);
        console.log(this.props.net)
        // console.log("bottle cost", bottleCost);
        // console.log("q)", quantity)
        return (
            <div>
               <Grid.Row>
    <Grid.Column width={8}>
  <Table celled>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell colSpan="2">Pricing</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
        <Table.Row >
            <Table.Cell>Case Price</Table.Cell>
            <Table.Cell>${this.props.price}</Table.Cell>
        </Table.Row>

        <Table.Row >
            <Table.Cell>Case Format</Table.Cell>
            <Table.Cell>{this.props.caseSize}</Table.Cell>
        </Table.Row>

        <Table.Row >
            <Table.Cell>Price/Bottle</Table.Cell>
            <Table.Cell>${bottleCost}</Table.Cell>
        </Table.Row>
        <Table.Row >
            <Table.Cell>Pricing Structure</Table.Cell>
            <Table.Cell>{this.props.net}</Table.Cell>
        </Table.Row>
        {/* {this.props.net === "Discountable" &&
    <Table.Row>
        <Table.Cell>Pricing Structure</Table.Cell>
        <Table.Cell>is Discount yes</Table.Cell>
    </Table.Row>
} */}

    </Table.Body>
   </Table>
   </Grid.Column>
  </Grid.Row> 
            </div>
        );
    }
}

export default Pricing;
