import React, { Component } from "react";
import HomeHeading from "../components/Homepage/HomeHeading/HomeHeading";
import Carousel from "../components/common/Carousel/Carousel";
import { Link } from "react-router-dom";
import API from "../utils/API";
import Winecard from "../components/WineCard"

import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Image,
  Segment,
} from 'semantic-ui-react';
import { createCipher } from "crypto";


class Home extends Component {
  state = {
    wines: []
  };
  //onload we get all the wine and producer data


  componentDidMount() {

    this.loadWines();
  }
  loadWines = () => {
    API.newWines()
      .then(res => {
        this.setState({ wines: res.data })
      }
      )
      .catch(err => console.log(err));
  };

  render() {
    return (
<Grid>

  <Carousel>
    <HomeHeading />
  </Carousel>

  <Grid.Row>
          <Grid.Column width={8}>
            <Header as='h3' style={{ fontSize: '2em' }}>
              Wine Wise: The Vienna Wine Company
            </Header>
            <p style={{ fontSize: '1.33em' }}>
              Since 1989, Wine Wise has been importing some of the finest estate-grown wines from France, Germany, Austria, Spain, Portugal and Greece. Our speciality is small family-firms that offer over-performing wines and exceptional value. Grower Champagnes and other sparkling wines are a particular area of emphasis for Wine Wise.
            </p>

          </Grid.Column>
          <Grid.Column floated='right' width={6}>

            <Image bordered rounded size='large' src='/images/winePouring.jpg' />
          </Grid.Column>
        </Grid.Row>

        <Grid.Row>
        <Header as='h2'>Just Arrived!</Header>
      </Grid.Row>

      <Grid.Row columns={4}>

      { (this.state.wines.Just_In === "Yes") ?    this.state.wines.map(wine => (
                      <Winecard header={wine.Wine} region={wine.Region} producer={wine.Producer} country={wine.Country} wineid={wine._id} key={wine._id} url={wine.URL} Code={wine.Code} />
                    ))
            :
            // Else Renders specific wine image from url:
            null
            } 

                    {/* {this.state.wines.map(wine => (
                      <Winecard header={wine.Wine} region={wine.Region} producer={wine.Producer} country={wine.Country} wineid={wine._id} key={wine._id} url={wine.URL} Code={wine.Code} />
                    ))} */}
                  </Grid.Row>

</Grid>
    );
  }
}

export default Home;

