import React, {Component} from 'react'
import { Link } from "react-router-dom";
import { Card, Image } from 'semantic-ui-react'
// import {Moon, Female, Leaf, Sun} from '../../components/Labels/Labels'
import "./Winecard.css";

class Winecard extends Component {

render(){

 imageCheck = () => {
  if( `/images/bottle/${this.props.Code}.png` == null){
    return false;
  } else {
    return true;
  }
  };

  return(
    <Link to={"/details/" + this.props.wineid} >

    <Card className="card" >

      {(this.props.url === "") ? <Image className="cardImage"src='/images/StockRED.png' />
        :
        // Else Renders row with image from url:
        // <Image className="cardImage" src={`https://gdurl.com${this.props.url}`} />

        // For rendering image from individual folder:
        <Image className="cardImage" src={`/images/bottle/${this.props.Code}.png`}/>
      }
 {/* CORNER LABELS */}

  {/* { ((this.props.farming === "Organic") || (this.props.farming === "Certified Organic" )) && <Leaf />}
  { ((this.props.farming === "Bio-dynamic") || (this.props.farming === "Certified Bio-dynamic" )) && <Moon />}
  { this.props.farming === "HEV" && <Sun />}
  { this.props.female === "Female Winemaker"  && <Female />} */}


      <Card.Content style={{textAlign: "center"}}>
        <Card.Header className='cardHeader'>{this.props.producer} {this.props.header}</Card.Header>
        <Card.Meta className='cardData'>
          <span>{this.props.region}, {this.props.country}</span>
        </Card.Meta>
      </Card.Content>
    </Card>
    </Link> 
  )
}


}
export default Winecard
